package week1.day1;

public class LearnDatatatype {
	
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte age=120;
		short salary=1000;
		int pincode=123333;
		long phonenumber=99962302L;
		float dataa=1.2f;
		double datab=1000.2500d;
		char a='a';
		String b="Pradeesh";
		boolean c=false;	
		System.out.println("Integer values:"+age+" "+salary+" "+pincode+" "+phonenumber);
		System.out.println("Float values:"+dataa+" "+datab);
		System.out.println("Char values:"+a+" "+b);
		System.out.println("Boolen values:"+c);
		

	}

}
